mapper.select:
SELECT COUNT(*) FROM "BOARD"
WHERE MEMBER_NO = #{memberNo}
AND BOARD_DEL_FL = 'N'

SELECT COUNT(*) FROM "RECIPE"
WHERE MEMBER_NO = #{memberNo}
AND RECIPE_DEL_FL = 'N'

mapper.update:
UPDATE "MEMBER" SET
MEMBER_NICKNAME = #{memberNickname},
MEMBER_INTRO = #{memberIntro}
WHERE MEMBER_NO = #{memberNo}
